
def is_pattern_exist(string:str, pattern:str):
    string_len = len(string)
    pattern_len = len(pattern)
    for index, char in enumerate(string):
        if pattern[0] == char and (string_len - index) >= pattern_len:
            pattern_exist = True
            for index_p, char_p in enumerate(pattern):
                if char_p != string[index + index_p]:
                    pattern_exist = False
            if pattern_exist:
                return pattern_exist
    return False

def find_substring(main_string:str, sub_string:str):
    main_len = len(main_string)
    sub_len = len(sub_string)
    for index in range(main_len - sub_len + 1):
        if main_string[index] == sub_string[0]:
            pattern_exist = True
            for sub_index in range(sub_len):
                if main_string[index + sub_index] != sub_string[sub_index]:
                    pattern_exist = False
                    break
            if pattern_exist:
                return index
    return -1



assert False == is_pattern_exist('Hello', 'pp')
assert False == is_pattern_exist('Hello', '0')
assert False == is_pattern_exist('Hello', 'oo')
assert False == is_pattern_exist('Hello', 'HH')
assert True  == is_pattern_exist('Hello', 'H')
assert True == is_pattern_exist('Hello', 'll')
assert True == is_pattern_exist('Hello', 'lo')
assert True == is_pattern_exist('Hello', 'o')

print(find_substring('Hello', '0'))
assert -1 == find_substring('Hello', 'pp')
assert -1 == find_substring('Hello', '0')
assert -1 == find_substring('Hello', 'oo')
assert -1 == find_substring('Hello', 'HH')

assert 0  == find_substring('Hello', 'H')
assert 2 == find_substring('Hello', 'll')
assert 3 == find_substring('Hello', 'lo')
assert 4 == find_substring('Hello', 'o')

